// Dependencies
import React, { Component } from 'react'
import fetch from "isomorphic-fetch";

class channel extends Component {
    static async getInitialProps({ query }) {
        let idChannel = query.id;
        let reqChannel = await fetch(`https://api.audioboom.com/channels/${idChannel}`);
        let dataChannel = await reqChannel.json();
        let channel = dataChannel.body.channel;

        let reqAudios = await fetch(`https://api.audioboom.com/channels/${idChannel}/audio_clips`);
        let dataAudios = await reqAudios.json();
        let audioClips = dataAudios.body.audio_clips;

        let reqSeries = await fetch(`https://api.audioboom.com/channels/${idChannel}/child_channels`);
        let dataSeries = await reqSeries.json();
        let series = dataSeries.body.channels;

        return { channel, audioClips, series }
    }

    render() {
        const { channel, audioClips, series } = this.props;

        return (
            <div>
                <header> Podcasts</header>
                <h2> { channel.title }</h2>

                <h2> Series </h2>
                {
                    series.map((serie) => (
                        <div>
                            { serie.title }
                        </div>
                    ))
                }

                <h2> Podcast recientes </h2>
                {
                    audioClips.map((clip) => (
                        <div>
                            { clip.title }
                        </div>
                    ))
                }
                <style jsx>
                    {`
                    :global(body) {
                        margin: 0;
                        font-family: system-ui;
                        background: white;
                        text-align: center
                    };
                    
                    header {
                        color: #fff;
                        background: #8756ca;
                        padding: 15px;
                    }
                    
                    .channels {
                      display: grid;
                      grid-gap: 15px;
                      padding: 15px;
                      grid-template-columns: repeat(auto-fill, minmax(160px, 1fr));
                    }
                    a.channel {
                      display: block;
                      margin-bottom: 0.5em;
                      color: #333;
                      text-decoration: none;
                    }
                    .channel img {
                      border-radius: 3px;
                      box-shadow: 0px 2px 6px rgba(0,0,0,0.15);
                      width: 100%;
                    }
                    h2 {
                      padding: 5px;
                      font-size: 0.9em;
                      font-weight: 600;
                      margin: 0;
                      text-align: center;
                    }
                 `}
                </style>
            </div>
        );
    }
}

export default channel
